import { useState } from "react";

function SidePanel(props)
{
    const [toggle1, setToggle1] = useState(true)
    const [toggle2, setToggle2] = useState(true)
    const [toggle3, setToggle3] = useState(true)
    const [toggle4, setToggle4] = useState(true)

    let objects = new Array();


    function filter3()
    {
        toggle3 ? setToggle3(false) : setToggle3(true)
        if(toggle3)
        {
            props.props.map((val)=>{
                if(val.price >= 100 && val.price < 1000)
                {
                    objects.push(val)
                }
            })
        }
    }

    function filter()
    {
        // if(toggle1)
        // {
        //     filter1()
        // }
        // if(toggle2)
        // {
        //     filter2()
        // }
        if(toggle3)
        {
            filter3()
        }
        // if(toggle4)
        // {
        //     filter4()
        // }
        if(toggle1 === false && toggle1 === false && toggle3 === false && toggle4 === false)
        return props.getState(objects)
    }
    return (
        <div class="col-2">
            <h5>Filter on price</h5>
            <input type="checkbox" class="form-check-input"/> <span>0-50</span> <br/>
            <input type="checkbox" class="form-check-input"/> <span>50-100 </span> <br/>
            <input type="checkbox" value="true" id="upto1000" class="form-check-input" onClick={filter}/> <span>100-1000</span> <br/>
            <input type="checkbox" class="form-check-input"/> <span>&gt; 1000</span><br/>
        </div>
    )
}

export default SidePanel;
